import React from 'react';
import './top.css';
import ConstantsList from './constant';
import { render } from '@testing-library/react';
//import test from './test';

export class Top extends React.Component{
    load(){
        
    }
    
    render(){
        return(
            <div className='bulletin_board'>
            <Linklist
                />
            <div className='new_thread_btn'>
                <button className='' 
                onClick={'location.href=' + ConstantsList.NEW_THREAD_BTN_LINK + '"'}>
                    {ConstantsList.NEW_THREAD_BTN_NAME}
                </button>
            </div>
                <Thread 
                />
            </div>
        );
    }
}
function Linklist(){
    /*DB読み込み*/

    //スレッドタイトルの配列を設定
    const thread_title_list = [{title:"test1"},{title:"test2"}];
    let link = thread_title_list.map.set("url","");
    link.url = link.map(
        (thread_title_list)=> ConstantsList.THREAD_DETAIL_LINK 
        + '?' 
        + thread_title_list.title
    );

    render();{
        return(
            <div>
                {link.map((link) => (
                    <a href={ConstantsList.THREAD_DETAIL_LINK + '?' +link.title}>
                            {link.title}
                        </a>
                    ))
                }
            </div>
        )
    }

}


function Thread(){
    /*DB読み込み*///{this.thread_title}

    render();{
        return(
            <div className='thread'>
                <h3 className='thread_title'>スレッドタイトル</h3>

            </div>
        );
    }
}

export default Top;
